const Board = () => {
  const renderCells = () => {
    const rows = [];
    let currentRow = [];
    let count = -10;
    for (let i = 0; i < 10; i++) {
      for (let j = 0; j < 10; j++) {
        if (i % 2 === 0) {
          currentRow.push(count);
        } else {
          currentRow.unshift(count);
        }
        count++;
      }
      rows.push(currentRow);
      currentRow = [];
    }
    return rows.map((row, index) => (
      <div className={`row row-${index}`} key={index}>
        {row.map((cell) => (
          <div className={`cell cell-${cell}`} key={cell}>
            {cell}
          </div>
        ))}
      </div>
    ));
  };

  return <div className="board">{renderCells()}</div>;
};

  export default Board;
