import React, { useState ,useEffect} from "react";
import "./../css/components/hand.css";

export default function Hand({
  type = "rock",
  left,
  color = "yellow",
  active,
  moving ,
}) {
  let activeHand = left ? "activeLeft" : "activeRight";
  // console.log(activeHand);
  // console.log("ayush",type,color);
  const[timer,setTimer]=useState(true)
  useEffect(() => {
  if (activeHand==="activeLeft") {
    setTimeout(()=>{
      setTimer(false)
    },5000)
  }
  }, [])
 
  return (

    <div className="hand">
    {
      timer ?
      <img
      className={[
        left ? "image userChoice" : "image computerChoice",
        moving ? "image moving" : "",
        active ? "" : activeHand,
      ].join(" ")}
      src={require(`./../image/${color}-rock.png`)}
      alt="hand"
    />
  
      :

      <img
      className={[
        left ? "image userChoice" : "image computerChoice",
        moving ? "image moving" : "",
        active ? "" : activeHand,
      ].join(" ")}
      src={require(`./../image/${color}-${type ? type : 'rock'}.png`)}
      alt="hand"
    />
  


    }
     </div>
  );
}
