import React from 'react';
import './board.css';

const Board = ({ size, children }) => {
  const renderCells = () => {
    const cells = [];
    for (let i = size; i >= 1; i--) {
      let content = i;
      if (i % 10 === 0) {
        content = i - 9;
        cells.push(<div key={`ladder-${i}`} className={`ladder ladder-${i}`} />);
      } else if (i % 10 === 1) {
        content = i + 9;
        cells.push(<div key={`snake-${i}`} className={`snake snake-${i}`} />);
      }
      cells.push(<div key={i} className={`cell cell-${i}`}>{content}</div>);
    }
    return cells.reverse();
  };

  return (
    <div className="board">
      {renderCells()}
      {children}
    </div>
  );
};

export default Board;
