  import React from 'react';
import './board.css';

const Board = ({ size, children }) => {
  const renderCells = () => {
    const cells = [];
    let num = size;
    let isIncreasing = false;
    
    for (let i = 1; i <= size; i++) {
      if (num === 0) {
        break;
      }
      
      cells.push(<div key={num} className={`cell cell-${num}`}>{num}</div>);
      num--;
      
      if (i % 10 === 0) {
        if (isIncreasing) {
          num -= 10;
          isIncreasing = false;
        } else {
          num -= 9;
          isIncreasing = true;
        }
      }
    }
    
    return cells;
  };
  
  return (
    <div className="board">
      {renderCells()}
      {children}
    </div>
  );
};

export default Board;
