import React from "react";
import style from "./../css/components/hand.css";

export default function Hand({
  type = "rock",
  left,
  color = "yellow",
  active,
  moving = false,
}) {
  let activeHand = left ? "activeLeft" : "activeRight";

  return (
    <div className="hand">
      <img
        className={[
          left ? "image playerOne" : "image playerTwo",
          moving ? "image moving" : "image",
          active ? "" : activeHand,
        ]}
        src={`./../image/${color}-${type}.png`}
      />
    </div>
  );
}
