import React, { useState ,useEffect} from "react";
import "./../css/components/hand.css";

export default function Hand({
  type = "rock",
  left,
  color = "yellow",
  active,
  moving = false ,
}) {
  let activeHand = left ? "activeLeft" : "activeRight";
  const [timer, setTimer] = useState(true);
  const [isMoving, setIsMoving] = useState(false);

  useEffect(() => {
    if (activeHand === "activeLeft") {
      setTimeout(() => {
        setIsMoving(true);
        setTimer(false);
        console.log("1");
      }, 5000);
    }
  }, [activeHand]);

  return (
    <div className="hand">
        
        <img
          className={[
            left ? "image userChoice" : "image computerChoice",

            isMoving ? "image moving" : "",
            active ? "" : activeHand,
          ].join(" ")}
          src={require(`./../image/${color}-${type ? type : 'rock'}.png`)}
          alt="hand"
        />
    </div>
  );
}
