import React from 'react';
import './board.css';

const Board = ({ size, children }) => {
const renderCells = () => {
const cells = [];
for (let i = size; i >= 1; i--) {
cells.push(<div key={i} className="cell">{i}</div>);
}
return cells;
};
return (
    <div className="board">
      {renderCells()}
      {children}
    </div>
  );
};

  export default Board;
