import { useState, useEffect } from "react";
import Hand from "../components/hand";
export default function Picker1({ active, color = "yellow" }) {
  const [isActive, setActive] = useState(true);
  const [userChoice, setUserChoice] = useState(null);
  const [computerChoice, setComputerChoice] = useState(null);
  const [result, setResult] = useState(null);

  function choice(choice) {
    setUserChoice(choice);
    setActive(false);
    // Call computerChoose here to get the computer's choice
    setComputerChoice(computerChoose());
  }

  function computerChoose() {
    const choices = ["rock", "paper", "scissors"];
    const randomIndex = Math.floor(Math.random() * choices.length);
    const choice = choices[randomIndex];
    // Return the choice instead of assigning it to a variable
    return choice;
  }

  function determineWinner(userChoice, computerChoice) {
    if (userChoice === computerChoice) {
      return "It's a tie!";
    } else if (
      (userChoice === "rock" && computerChoice === "scissors") ||
      (userChoice === "paper" && computerChoice === "rock") ||
      (userChoice === "scissors" && computerChoice === "paper")
    ) {
      return "You win!";
    } else {
      return "Computer wins!";
    }
  }

  useEffect(() => {
    if (userChoice && computerChoice) {
      setResult(determineWinner(userChoice, computerChoice));
    }
  }, [userChoice, computerChoice]);

  return (
    <>
      <div className={isActive ? "picker active" : "picker"}>
        <div className="options">
          <img
            onClick={() => choice("rock")}
            className="image"
            src={require(`./../image/${color}-rock.png`)}
            alt="rock"
          />
          <img
            onClick={() => choice("paper")}
            className="image"
            src={require(`./../image/${color}-paper.png`)}
            alt="paper"
          />
          <img
            onClick={() => choice("scissors")}
            className="image"
            src={require(`./../image/${color}-scissors.png`)}
            alt="scissors"
          />
        </div>
      </div>
      <div className="hands">
        <Hand
          left={true}
          color={playerOne?.color}
          type={winner ? playerOne?.choice : "rock"}
          active={playerOne?.choice ? true : false}
          moving={
            playerOne?.choice && playerTwo?.choice
              ? winner
                ? false
                : true
              : false
          }
        />
        <Hand
          left={false}
          color={playerTwo?.color}
          type={winner ? playerTwo?.choice : "rock"}
          active={playerTwo?.choice ? true : false}
          moving={
            playerOne?.choice && playerTwo?.choice
              ? winner
                ? false
                : true
              : false
          }
        />
      </div>

    </>
  );
}
