import React, { useState ,useEffect} from "react";
import "./../css/components/hand.css";

export default function Hand({
  type = "rock",
  left,
  color = "yellow",
  active,
  moving = false ,
  onPlayerChoose,
}) {
  let activeHand = left ? "activeLeft" : "activeRight";
  const [timer, setTimer] = useState(true);
  const [isMoving, setIsMoving] = useState(false);

  useEffect(() => {
    if (isMoving) {
      setTimeout(() => {
        setIsMoving(false);
        setTimer(true);
        onPlayerChoose(type);
      }, 2000);
    }
  }, [isMoving, type, onPlayerChoose]);

  const handleClick = () => {
    setIsMoving(true);
  };

  return (
    <div className="hand" onClick={handleClick}>
      {timer ? (
        <img
          className={[
            left ? "image userChoice" : "image computerChoice",
            active ? "" : activeHand,
          ].join(" ")}
          src={require(`./../image/${color}-rock.png`)}
          alt="hand"
        />
      ) : (
        <img
          className={[            left ? "image userChoice" : "image computerChoice",            isMoving ? "image moving" : "",            active ? "" : activeHand,          ].join(" ")}
          src={require(`./../image/${color}-${type ? type : 'rock'}.png`)}
          alt="hand"
        />
      )}
    </div>
  );
}
