import { useState, useEffect } from "react";
import Hand from "../components/hand";

export default function Picker1({ active, draw }) {
  const [isActive, setActive] = useState(true);
  const [userChoice, setUserChoice] = useState(null);
  const [computerChoice, setComputerChoice] = useState(null);
  const [winner, setWinner] = useState(null);

  function choice(choice) {
    setUserChoice(choice);
    setActive(false);
    const computer = computerChoose();
    setComputerChoice(computer);
    setWinner(determineWinner(choice, computer));
  }

  function computerChoose() {
    const choices = ["rock", "paper", "scissors"];
    const randomIndex = Math.floor(Math.random() * choices.length);
    return choices[randomIndex];
  }

  function determineWinner(userChoice, computerChoice) {
    if (userChoice === computerChoice) {
      return "draw";
    } else if (
      (userChoice === "rock" && computerChoice === "scissors") ||
      (userChoice === "paper" && computerChoice === "rock") ||
      (userChoice === "scissors" && computerChoice === "paper")
    ) {
      return "user";
    } else {
      return "computer";
    }
  }

  useEffect(() => {
    if (userChoice && computerChoice) {
      setWinner(determineWinner(userChoice, computerChoice));
    }
  }, [userChoice, computerChoice]);

  function resetGame() {
    setActive(true);
    setUserChoice(null);
    setComputerChoice(null);
    setWinner(null);
  }

  return (
    <>
      <div className="results">
  {winner !== null && (
    <div className={winner ? "wrapper active" : "wrapper"}>
      {winner === "draw" ? (
        <div className="text">
          <span>It's a draw!</span>
          <button onClick={resetGame}>Play Again</button>
        </div>
      ) : (
        <div className="text">
          <span>{winner === "user" ? "You" : "Computer"}</span>
          <span> won!</span>
          <button onClick={resetGame}>Play Again</button>
        </div>
      )}
    </div>
  )}
</div>

      <div className={isActive ? "picker active" : "picker"}>
        <div className="options">
          <img
            onClick={() => choice("rock")}
            className="image"
            src={require(`./../image/yellow-rock.png`)}
            alt="rock"
          />
          <img
            onClick={() => choice("paper")}
            className="image"
            src={require(`./../image/yellow-paper.png`)}
            alt="paper"
          />
          <img
            onClick={() => choice("scissors")}
            className="image"
            src={require(`./../image/yellow-scissors.png`)}
            alt="scissors"
          />
        </div>
      </div>
      <div className="hands">
        <Hand
          left={true}
          type={winner === "user" ? userChoice : "rock"}
          active={userChoice ? true : false}
          moving={userChoice && computerChoice && winner === null}
       /><Hand
          left={false}
          type={winner === "computer" ? computerChoice : "rock"}
          active={computerChoice ? true : false}
          moving={userChoice && computerChoice && winner === null}
        />
      </div>
    </>
     );
     }
