const socket = io.connect("http://localhost:8001");

function App() {



  //Room State
  const [room, setRoom] = useState("");

  // Messages States
  const [message, setMessage] = useState("");
  const [messageReceived, setMessageReceived] = useState("");

  const joinRoom = () => {
    if (room !== "") {
      socket.emit("join_room", room);
    }
  };
  console.log("r", room, message);
  let stored = localStorage.getItem("room");

  var flag = {
    room: room,
    message: message,
  };

  var arr = [];
  arr.push(stored, flag);

  const sendMessage = () => {
    let msgObj = {
      message: message,
      room: room,
      send: true,
    };
    socket.emit("send_message", { msgObj, room });
    localStorage.setItem("room",JSON.stringify(arr))
  };

  useEffect(() => {
    alert("uppr")
    socket.on("testsocket", (data) => {
      console.log(data);
      alert("niche")
      setMessageReceived(data.msgObj.message);
      console.log("data", data);
      let msgObj = {
        message: data.msgObj.message,
        room: data.msgObj.room,
        receive: true,
      };
      console.log("received data", msgObj);
    });
  }, [socket]);

  return (
    <>
      <div className="App">
        <input
          placeholder="Room Number..."
          onChange={(event) => {
            setRoom(event.target.value);
          }}
        />
        <button onClick={joinRoom}>Join Room</button>
        <input
          placeholder="Message..."
          onChange={(event) => {
            setMessage(event.target.value);
          }}
        />
        <button onClick={sendMessage}>Send Message</button>
        <h1> Message:</h1>

        <div id="message">
          <p id="history">
            "room" : {room}
            <br />
            "message" :{messageReceived}
          </p>
        </div>
      </div>
    </>
  );
}

export default App;
