import React, { useState } from 'react';
import './one.css';

const App = () => {
  const [playerPosition, setPlayerPosition] = useState(1);
  const [computerPosition, setComputerPosition] = useState(1);
  const [showResult, setShowResult] = useState(false);
  const [playerRoll, setPlayerRoll] = useState(0);
  const [computerRoll, setComputerRoll] = useState(0);
  const rollDice = () => {
    const playerRoll = Math.ceil(Math.random() * 6);
    const computerRoll = Math.ceil(Math.random() * 6);
    setPlayerPosition((position) => Math.min(position + playerRoll, 100));
    setComputerPosition((position) => Math.min(position + computerRoll, 100));
    setShowResult(true);
  };

  const renderBoard = () => {
    const cells = [];

    for (let i = 0; i < 100; i++) {
      const cellNumber = i + 1;
      const isActive = playerPosition === cellNumber || computerPosition === cellNumber;
      const ladder = ladderList.find((ladder) => ladder.start === cellNumber);
      const snake = snakeList.find((snake) => snake.start === cellNumber);

      cells.push(
        <div key={i} className={`cell ${isActive ? 'active' : ''}`}>
          {ladder && <div className="ladder" style={{ left: ladderLeft(ladder.start), top: ladderTop(ladder.start) }} />}
          {snake && <div className="snake" style={{ left: snakeLeft(snake.start), top: snakeTop(snake.start) }} />}
          {cellNumber}
        </div>
      );
    }

    return cells;
  };

  const ladderLeft = (position) => {
    const column = (position - 1) % 10;
    const row = 9 - Math.floor((position - 1) / 10);
    const isEvenRow = row % 2 === 0;

    if (isEvenRow) {
      return `${column * 10 + 5}%`;
    } else {
      return `${(9 - column) * 10 + 5}%`;
    }
  };

  const ladderTop = (position) => {
    const row = 9 - Math.floor((position - 1) / 10);
    return `${row * 10}%`;
  };

  const snakeLeft = (position) => {
    const column = (position - 1) % 10;
    const row = 9 - Math.floor((position - 1) / 10);
    const isEvenRow = row % 2 === 0;

    if (isEvenRow) {
      return `${column * 10 + 5}%`;
    } else {
      return `${(9 - column) * 10 + 5}%`;
    }
  };

  const snakeTop = (position) => {
    const row = 9 - Math.floor((position - 1) / 10);
    return `${row * 10}%`;
  };

  const ladderList = [
    { start: 5, end: 25 },
    { start: 14, end: 40 },
    { start: 30, end: 67 },
    { start: 43, end: 82 },
    { start: 66, end: 97 },
  ];

  const snakeList = [
    { start: 31, end: 4 },
    { start: 37, end: 13 },
    { start: 51, end: 29 },
    { start: 65, end: 34 },
   
    { start: 74, end: 48 },
    { start: 89, end: 53 },
    { start: 98, end: 56 },
    ];
    
    const playerHasWon = playerPosition === 100;
    const computerHasWon = computerPosition === 100;
    
    return (
    <div className="App">
    <h1>Snake and Ladder</h1>
    <div className="board">{renderBoard()}</div>
    {!showResult && (
    <button className="roll-button" onClick={rollDice}>
    Roll Dice
    </button>
    )}
    {showResult && (
    <div className="result">
    <div>{`Player rolled a ${playerRoll}`}</div>
    <div>{`Computer rolled a ${computerRoll}`}</div>
    </div>
    )}
    {playerHasWon && <div className="win-message">You win!</div>}
    {computerHasWon && <div className="lose-message">You lose!</div>}
    </div>
    );
    };
    
    export default App;
