import React from "react";
import "./../css/components/hand.css";

export default function Hand({
  type = "rock",
  left,
  color = "yellow",
  active,
  moving = false,
}) {
  let activeHand = left ? "activeLeft" : "activeRight";
  console.log(activeHand);
  return (
    <div className="hand">
      <img
        className={[
          left ? "image playerOne" : "image playerTwo",
          moving ? "image moving" : "",
          active ? "" : activeHand,
        ].join(" ")}
        src={require(`./../image/${color}-${type}.png`)}
        alt="hand"
      />
    </div>
  );
}
