import React from 'react';
import './board.css';

const Board = ({ size, children }) => {

  const temp = new Array(10);
  temp.fill(0);
  let begin = 110;

  return (
    <div className="App">
      {temp.map((_, i) => {
        begin = begin - 10;
        return (
          <div key={i} className="board-row">
            {temp.map((__, j) => {
              if (j !== 0) {
                i % 2 === 0 ? begin-- : begin++;
              }
              return (
                <div key={`${i}${j}cell`} className="board-cell">
                  {begin}
                </div>
              );
            })}
          </div>
        );
      })}
    </div>
  );
}
